    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed

        String nameUser = jTextName.getText();
        String passwordUser = jTextPassword.getText();
     
        
        String name = "Admin";
        String password = "1234";
        
        if (name.equals(nameUser)) {
            if (password.equals(passwordUser)) {
                MenuPrincipal abrirMenuPrincial = new MenuPrincipal();
                abrirMenuPrincial.setVisible(true);
                this.setVisible(false);
            } else {
                campoR2.setVisible(true);
            }
        } else {
            campoR1.setVisible(true);
        }


    }//GEN-LAST:event_jButton1ActionPerformed

